    correctedNeuvacIrr = []
    for i in range(neuvacIrr.shape[-1]):
        correctedNeuvacIrr.append(neuvacIrr[:, i] / meanFactors[i])
    correctedNeuvacIrr = np.asarray(correctedNeuvacIrr).T

